from textblob import TextBlob

def senti(text):
    blob = TextBlob(text)

    for sentence in blob.sentences:
        if sentence.sentiment.polarity > 0:
            return 'Positive'
        elif sentence.sentiment.polarity < 0:
            return 'Negative'
        else:
            return 'Neutral'

print senti("this is a good test")
print senti("this is a bad test")
print senti("the brown fox")
